//setup data layer
//we need this to track basket

import React, { createContext, useContext, useReducer } from 'react';

//THIS IS THE DAYA LAYER
export const StateContext = createContext();

//BUILD A PROVIDER  so that we can wrap our entire app inside this provider and gives access to StateContext
export const StateProvider = ({ reducer, initialState, children }) => (
    <StateContext.Provider value={useReducer(reducer, initialState)}>
        {children}
    </StateContext.Provider>
)

//this is how we use of it inside of a component
export const useStateValue = () => useContext(StateContext);
