import React from 'react'
import { useStateValue } from './StateProvider';
import './Checkout.css'

function Checkout() {

    const [{ basket }] = useStateValue();

    return (
        <div className="checkout">
            <img className="checkout__add" src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQLVnaP6qC7z_EycyjakLHmI_GX4iZngL6b1g&usqp=CAU" alt=""></img>

            {basket?.length === 0 ? (
                <div>
                    <h2>Your shopping Cart is Empty!</h2>
                </div>
            ) : (
                <div>
                    <h2>Your Shopping Basket</h2>
                </div>
            )}

        </div>
    )
}

export default Checkout
